// Input example: "t:hello world"
const text = $json.message.text || "";

// Extract everything after "t:"
const result = text.replace(/^t:\s*/, ""); 

return { result };

// Take whichever exists: message or result
const messagelast = $json.message || $json.result;

// Output
return [{ json: { messagelast } }];

const input = $json;

// Safely extract text from Telegram payload
const text = input.message?.text || input.body?.message?.text || "";

// Extract everything after "p:" (case-insensitive, trims spaces)
const result = text.replace(/^p:\s*/i, "").trim();

// Return a single object for this item
return {
  json: {
    message: result || "No text found",
  },
};

const out = [];

for (const item of items) {
  const ok = item.json?.ok === undefined ? true : item.json.ok === true;
  const binKeys = item.binary ? Object.keys(item.binary) : [];
  const firstBinKey = binKeys[0];
  const hasBinary = !!firstBinKey;

  if (ok && hasBinary) {
    // Clone to preserve raw base64 buffer
    const binaryData = item.binary[firstBinKey];

    out.push({
      binary: {
        data: binaryData,
      },
    });
  }
}

return out;
