// === Step 1: Get Telegram message ===
const textData = $items("Filter Text from @n8nInputTrigger")[0].json;
const message = textData.message?.trim() || "Inner peace.";
const chat_id = textData.chat_id || 360503313;

// === Step 2: Get Unsplash image URL ===
const imageData = $items("Fetch Nature Image")[0].json;
const unsplashUrl =
  imageData.urls?.regular ||
  "https://images.unsplash.com/photo-1506744038136-46273834b3fb";

// === Step 3: Clean and encode text ===
const cleanedText = message.replace(/"/g, '').trim();
const encodedText = encodeURIComponent(cleanedText);

// === Step 4: Build ImageKit URL (only overlay text) ===
// ImageKit will fetch the Unsplash image as-is and just overlay your text.
const baseEndpoint = "https://ik.imagekit.io/meorhakimi/External/";
const encodedUnsplashUrl = encodeURIComponent(unsplashUrl);

// ot → overlay text
// ots → text size (48px)
// otc → text color (white)
// otbg → semi-transparent black background
// otp → position (50% x, 90% y)
const imagekitURL = `${baseEndpoint}${encodedUnsplashUrl}?tr=ot-${encodedText},ots-48,otc-FFFFFF,otbg-00000080,otp-50_90`;

// === Step 5: Return single object ===
return {
  json: {
    chat_id,
    message,
    unsplashUrl,
    quotedImage: imagekitURL
  }
};

